import networkimport timeimport gcimport hardwaresta = network.WLAN(network.STA_IF)ap = network.WLAN(network.AP_IF)def main():    gc.enable()    hardware.oled_clear()    hardware.oled_text("Starting...", 16, 24)    hardware.pixel_color(64, 0, 64)    while True:        sta.active(True)        starting_time = time.time()        while not sta.isconnected() and (time.time() - starting_time) < 10:            pass        if not sta.isconnected():            time.sleep(5)            import config            config.doConfigWork()            ap.active(False)        else:            time.sleep(5)            import widget            widget.doWidgetWork()        gc.collect()