import hardwaredef test1(hw):    hw.oled_clear()    hw.oled_graphic('/graphics/yingyang.txt', 48, 16)    hw.oled_show()def test2(hw):    hw.oled_clear()    hw.oled_graphic('/graphics/splash.txt', 0, 16)    hw.oled_show()def test3(hw):    hw.oled_clear()        hw.oled_graphic('/graphics/n1.txt', 8, 16)    hw.oled_graphic('/graphics/n2.txt', 32, 16)    hw.oled_graphic('/graphics/colon.txt', 56, 16)    hw.oled_graphic('/graphics/n5.txt', 72, 16)    hw.oled_graphic('/graphics/n8.txt', 100, 16)    #hardware.oled_box(0, 0, 127, 63)    hw.oled_show()def main():    hw = hardware.Hardware()        while True:        if hw.button1_pressed():            test1(hw)        if hw.button2_pressed():            test2(hw)        if hw.button3_pressed():            test3(hw)