import widget                def next_color(color_number, intensity):            if color_number == 0:        current_color = (intensity, 0, 0)    elif color_number == 1:        current_color = (0, intensity, 0)    elif color_number == 2:        current_color = (0, 0, intensity)    elif color_number == 3:        current_color = (intensity, intensity, intensity)    return current_colordef main():    count = 0    color_number = 0    intensity = 64    current_color = (intensity, intensity, intensity)    light_on = False        while True:                if widget.button1_pressed():            widget.pixel_color(current_color[0],current_color[1],current_color[2])            light_on = True            #widget.oled_graphic(40, 5)                    if widget.button2_pressed():            widget.pixel_color(0,0,0)                            light_on = False            widget.oled_clear()                    if widget.button3_pressed():            current_color = next_color(color_number, intensity)            if color_number >= 3:                color_number = 0            else:                color_number += 1            if light_on:                widget.pixel_color(current_color[0],current_color[1],current_color[2])            widget.oled_clear()            widget.oled_circle(64, 32, 10)            widget.oled_circle(64, 32, 12)            widget.oled_line(0, 0, 123, 0)            widget.oled_line(123, 0, 123, 63)            widget.oled_line(0, 0, 0, 63)            widget.oled_line(0, 63, 123, 63)            widget.oled_line(0, 0, 123, 63)            widget.oled_line(0, 63, 123, 0)        if ((count % 100000) == 0 and light_on):            #widget.oled_clear()            widget.oled_text(str(count), 0, 0)            widget.oled_text(str(count), 8, 8)            widget.oled_text(str(count), 16, 16)            widget.oled_text(str(count), 24, 24)            widget.oled_text(str(count), 32, 32)            widget.oled_text(str(count), 40, 40)            widget.oled_text(str(count), 48, 48)            widget.oled_text(str(count), 56, 56)                    count+=1